import axios from "axios";

const BASE_URL = "http://localhost:8080/pessoas";

class PessoasService {
  getAllPessoas() {
    return axios.get(BASE_URL);
  }

  createPessoas(pessoas) {
    return axios.post(BASE_URL + "/" + pessoasId);
  }

  getPessoasById(pessoasId) {
    return axios.get(BASE_URL + "/" + pessoasId);
  }

  updatePessoas(pessoasId, pessoas) {
    return axios.put(BASE_URL + "/" + pessoasId, pessoas);
  }

  deletePessoas(pessoasId) {
    return axios.delete(BASE_URL + "/" + pessoasId);
  }
}

export default new PessoasService();
