import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import PessoasService from "../../services/PessoasService";

export default function Index() {
  const [pessoas, setPessoas] = useState([]);

  const getAllPessoas = () => {
    PessoasService.getAllPessoas()
      .then((response) => {
        setPessoas(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getAllPessoas();
  }, []);

  const deletePessoas = (pessoasId) => {
    PessoasService.deletePessoas(pessoasId)
      .then((response) => {
        getAllPessoas();
      })
      .catch((error) => {
        console.log(error);
        const { data } = error.response;
        if (data.status === 500) {
          alert("Erro na API");
        }
      });
  };

  return (
    <>
      <header className="header">
        <h1 className="container">Cadastro</h1>
      </header>
      <div className="container p-5">
        <Link to="/Cadsatro-Create" className="btn btn-primary mb-2">
          Cadastrar
        </Link>
        <div className="table-responsive">
          <table className="table table-hover table-sm">
            <thead>
              <tr>
                <th>Id</th>
                <th>Nome</th>
                <th>CPF</th>
                <th>Opções</th>
              </tr>
            </thead>
            <tbody>
              {pessoas.map((pessoas) => (
                <tr key={pessoas.id_pessoas}>
                  <td>{pessoas.id_pessoas}</td>
                  <td>{pessoas.nome}</td>
                  <td>{pessoas.cpf}</td>
                  <td className="d-flex">
                    <Link
                      to={`/Pessoas-Update/${pessoas.id_pessoas}`}
                      className="btn btn-info"
                    >
                      Editar
                    </Link>
                    <button
                      className="btn btn-danger"
                      onClick={() => deletePessoas(pessoas.id_pessoas)}
                      style={{ marginLeft: "10px" }}
                    >
                      Deletar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}
