import axios from "axios";

const BASE_URL = "http://localhost:8080/passagens";

class PassagensService {
  getAllPassagens() {
    return axios.get(`${BASE_URL}`);
  }

  createPassagens(passagens) {
    return axios.post(BASE_URL, passagens);
  }

  getPassagensById(passagensId) {
    return axios.get(BASE_URL + "/" + passagensId);
  }

  updatePassagens(passagensId, passagens) {
    return axios.put(BASE_URL + "/" + passagensId, passagens);
  }

  deletePassagens(passagensId) {
    return axios.delete(BASE_URL + "/" + passagensId);
  }
}

export default new PassagensService();
